import { createStore } from 'redux'
import { createWrapStore } from 'webext-redux'
import reducers from './reducers'
import { v4 as uuidV4 } from 'uuid'
import Configuration from './models/Configuration'
import Badge from './models/Badge'
import OptionalFeature from './models/OptionalFeature'
import NetRequestManager from './models/NetRequestManager'
import { logger, connectLogger } from './helpers/logger'
import LiveModeAlarm from './models/LiveModeAlarm'

try {
  ;(function (scope) {
    'use strict'

    let enabledHotkeyGroup = -1
    const badge = new Badge(scope.chrome.action, -1, ({ timer, tabs }) => {
      scope.chrome.storage.session.set({ badgeData: { timer, tabs } })
    })

    const netRequestManager = new NetRequestManager(scope.chrome.declarativeNetRequest, logger)

    const liveModeAlarm = new LiveModeAlarm(scope.chrome.alarms, logger, badge, ({ startTime }) => {
      scope.chrome.storage.session.set({ liveModeAlarmData: { startTime } })
    })

    // recover states
    scope.chrome.storage.session.get(
      {
        badgeData: {
          timer: -1,
          tabs: []
        },
        liveModeAlarmData: {
          startTime: -1
        },
        hotKeyGroupData: {
          enabledHotkeyGroup: -1
        }
      },
      ({ badgeData, netRequestManagerData, liveModeAlarmData, hotKeyGroupData }) => {
        badge.load(badgeData.tabs, badgeData.timer)
        liveModeAlarm.load(liveModeAlarmData.startTime)
        enabledHotkeyGroup = hotKeyGroupData.enabledHotkeyGroup
      }
    )

    liveModeAlarm.registerAlarmListener()

    scope.chrome.runtime.onInstalled.addListener(function () {
      scope.chrome.contextMenus.create({
        id: 'dmToggleLiveMode',
        title: 'Toggle Live Mode',
        contexts: ['action']
      })
      scope.chrome.contextMenus.create({
        id: 'dmToggleDebugMode',
        title: 'Toggle Debug Mode',
        contexts: ['action']
      })
    })

    scope.chrome.tabs.onCreated.addListener(function (tab) {
      logger('debug', 'New tab created, initialize badge.').write()
      badge.updateDemoCounter(0, tab.id)
    })

    /*
     * The following replaces the declarative content scripts, which require
     * high host permissions.
     */
    scope.chrome.tabs.onUpdated.addListener(function (tabId, changeInfo) {
      if (changeInfo.status !== 'loading') {
        return
      }
      scope.chrome.tabs.get(tabId, (tab) => {
        if (tab.url) {
          logger('debug', 'Trying to inject for', tabId, tab.url).write()
          scope.chrome.scripting.executeScript(
            {
              target: { tabId, allFrames: true },
              files: ['js/monkey.js'],
              injectImmediately: true
            },
            () => {
              logger('debug', 'Injection completed for', tabId, tab.url).write()
            }
          )
        } else {
          logger('warn', 'Did not inject into tab ', tabId, ': Permission denied').write()
          badge.updateDemoCounter('!')
        }
      })
    })

    scope.chrome.tabs.onRemoved.addListener(function (tabId) {
      logger('debug', 'Tab closed, cleaning up badge & netRequestManager', tabId).write()
      badge.removeTab(tabId)
      netRequestManager.removeTab(tabId)
    })

    scope.chrome.tabs.onRemoved.addListener(function (tab) {
      // TODO: netRequestManager & badge cleanup
    })

    scope.chrome.runtime.onMessage.addListener(function (request, sender, sendResponse) {
      if (request.receiver && request.receiver === 'background') {
        if (
          typeof request.count === 'number' &&
          typeof sender.tab === 'object' &&
          typeof sender.tab.id === 'number'
        ) {
          badge.updateDemoCounter(request.count, sender.tab.id)
        }
        if (request.task && request.task === 'addUrl' && typeof request.description === 'object') {
          netRequestManager.add(request.description, sender.tab.id)
        }
        if (
          request.task &&
          request.task === 'removeUrl' &&
          typeof request.description === 'object'
        ) {
          netRequestManager.remove(request.description, sender.tab.id)
        }
        if (request.task && request.task === 'clearUrls') {
          netRequestManager.clear()
        }
      }
    })

    // via https://github.com/colbat/webext-redux-event-driven/blob/main/background.js
    // Since we are in a service worker, this is not persistent
    // and this will be reset to false, as expected, whenever
    // the service worker wakes up from idle.
    let isInitialized = false

    const persistentStates = {
      configurations: [
        {
          name: 'Example',
          content: require('../examples/one.mnky'),
          test: 'Inventory-Services\nCart\nCART\nSan Francisco',
          enabled: false,
          values: {},
          id: uuidV4()
        },
        {
          name: 'templates/Cities',
          content: require('../examples/cities.mnky'),
          test: 'San Francisco\nSeattle\nLondon',
          enabled: false,
          values: {},
          id: uuidV4()
        }
      ],
      settings: {
        baseTemplate: require('../examples/baseTemplate.mnky'),
        analyticsSnippet: '',
        optionalFeatures: OptionalFeature.getDefaultValues(),
        globalVariables: [
          {
            key: 'myPurple',
            value: '7e69d2'
          },
          {
            key: 'dmLogo',
            value:
              'data:image/png;base64,'
          }
        ],
        debugMode: false,
        monkeyInterval: 100
      },
      monkeyID: uuidV4()
    }

    function init() {
      scope.chrome.storage.local.get(persistentStates, function (state) {
        if (!isInitialized) {
          // currentView is not persistent but should be defined to avoid
          // issues rendering the UI.
          // state.currentView = 'welcome'
          state.connectionState = 'unknown'
          state.settings.liveMode = false
          // We start with an empty log. Maybe in a later release persistance could be an idea..
          state.log = []
          run(state)
          isInitialized = true
        }
        scope.chrome.runtime.sendMessage({ type: 'STORE_INITIALIZED' })
      })
    }
    init()

    function updateStorage(store) {
      const configurations = store.getState().configurations
      const settings = store.getState().settings

      // Sync data back into chrome.storage
      scope.chrome.storage.local.set({
        configurations,
        settings,
        monkeyID: store.getState().monkeyID
      })
      if (
        settings.optionalFeatures.webRequestHook &&
        configurations.filter((c) => c.enabled).length > 0
      ) {
        netRequestManager.enable()
      } else {
        netRequestManager.disable()
      }
    }

    function run(state) {
      const store = createStore(reducers, state)
      const wrapStore = createWrapStore()
      wrapStore(store, { portName: 'DEMO_MONKEY_STORE' })

      // Make the store accessible from dev console.
      scope.store = store

      const settings = store.getState().settings

      if (settings.optionalFeatures.writeLogs) {
        connectLogger(store, { source: 'monkey.js' })
      }

      // Persist monkey ID. Shouldn't change after first start.
      scope.chrome.storage.local.set({ monkeyID: store.getState().monkeyID })

      if (
        settings.optionalFeatures.webRequestHook &&
        store.getState().configurations.filter((c) => c.enabled).length > 0
      ) {
        netRequestManager.enable()
      } else {
        netRequestManager.disable()
      }

      store.subscribe(function () {
        const lastAction = store.getState().lastAction

        logger('debug', 'Last action type', lastAction.type).write()

        switch (lastAction.type) {
          case 'APPEND_LOG_ENTRIES':
            return
          case 'TOGGLE_LIVE_MODE':
            liveModeAlarm.toggle()
            break
          default:
            updateStorage(store)
        }
      })

      function toggleHotkeyGroup(group) {
        const toggle = enabledHotkeyGroup !== group

        enabledHotkeyGroup = toggle ? group : -1

        // Persist enabled hotkey group.
        scope.chrome.storage.session.set({
          hotKeyGroupData: { enabledHotkeyGroup }
        })

        store.getState().configurations.forEach(function (c) {
          const config = new Configuration(c.content, null, false, c.values)
          if (config.isTemplate() || !config.isRestricted()) {
            return
          }

          if (Array.isArray(c.hotkeys) && c.hotkeys.includes(group)) {
            store.dispatch({
              type: 'TOGGLE_CONFIGURATION',
              id: c.id,
              enabled: toggle
            })
          } else if (c.enabled) {
            store.dispatch({ type: 'TOGGLE_CONFIGURATION', id: c.id })
          }
        })
      }

      scope.chrome.commands.onCommand.addListener(function (command) {
        logger('debug', 'Command:', command).write()

        if (command.startsWith('toggle-hotkey-group')) {
          const group = parseInt(command.split('-').pop())
          toggleHotkeyGroup(group)
        }
        if (command === 'live-mode') {
          store.dispatch({ type: 'TOGGLE_LIVE_MODE' })
        }
        if (command === 'debug-mode') {
          store.dispatch({ type: 'TOGGLE_DEBUG_MODE' })
        }
      })

      scope.chrome.contextMenus.onClicked.addListener((info, tab) => {
        const { menuItemId } = info
        if (menuItemId === 'dmToggleLiveMode') {
          store.dispatch({ type: 'TOGGLE_LIVE_MODE' })
        } else if (menuItemId === 'dmToggleDebugMode') {
          store.dispatch({ type: 'TOGGLE_DEBUG_MODE' })
        }
      })
    }
  })(globalThis)
} catch (e) {
  console.error(e)
}
